import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import Contact from './components/Contact';

function App() {
  const [contactsData, setContactsData] = useState(new Array(1000));
  const [dataLoaded, setDataLoaded] = useState(false);

  useEffect(() => {
    const getData = async () => {
      const data = await fetchData();
      setContactsData(data);
    };

    getData();
  }, []);

  const fetchData = async () => {
    const res = await fetch('https://teacode-recruitment-challenge.s3.eu-central-1.amazonaws.com/users.json');

    if (!res.ok) {
      const mess = `Something went wrong. Error: ${res.status}`;
      throw new Error(mess);
    }

    const data = await res.json();
    return data;
  };


  const handleChange = (id) => {
    console.log(id);
    console.log("xD");
  };




  return (
    <div className="container">
      <header>
        <div className="header">TeaCode APP</div>
      </header>

      <main>

          <ul className="main">
            {contactsData.map(contact => (
              <Contact contact={contact} handleChange={handleChange} key={contact.id}/>
            ))}
          </ul>

      </main>

      <footer>
        <div className="footer">
          Bartosz Kowal - TeaCode React.js Recruitment Challenge &copy 2021
        </div>
      </footer>      

    </div>
  );
}

export default App;
